CREATE TABLE IF NOT EXISTS "m_game" ("id" INTEGER NOT NULL , "name" VARCHAR(255) NOT NULL, "description" VARCHAR(255), "version" VARCHAR(255), "price" INTEGER, "url" VARCHAR(255), "artwork1Url" VARCHAR(255), "artwork2Url" VARCHAR(255), "artwork3Url" VARCHAR(255), "artistId" INTEGER, "artistName" VARCHAR(255), "averageRating" FLOAT, "ratingCount" INTEGER, "averageRatingForCurrentVersion" FLOAT, "ratingCountForCurrentVersion" INTEGER, "jsonText" TEXT NOT NULL, "createdAt" TIMESTAMP WITH TIME ZONE NOT NULL, "updatedAt" TIMESTAMP WITH TIME ZONE NOT NULL, PRIMARY KEY ("id"));